import './NavBar.css'
import { Link, NavLink } from 'react-router-dom';
import { useEffect, useState } from 'react';



const NavBarStyle = ({isActive}) => {
    return{
        textDecoration: 'none',
        color: '#0078d7',
        fontSize: '20px',
        backgroundColor: isActive ? "#2da9ad": '',
        borderRadius: "12px",
        paddingTop: '10px,',
        paddingBottom: '10px',
        width: '50px',

    }
}

function NavBar() {
    const [location, setLocation] = useState("")

    useEffect(() => {
        console.log(window.location.pathname)
        setLocation(window.location.pathname)
    }, [location])

    return (
        <div id="nav-container">
            <nav id="navBar">
                <div className="ul-cont">
                    <ul id="navLi-container">
                        <li className="nav-li">
                            <NavLink to="/" style={NavBarStyle}>Home</NavLink>
                        </li>
                        <li className="nav-li">
                            <NavLink to="/projects"  style={NavBarStyle}>Projects</NavLink>
                        </li>
                        <li className="nav-li">
                            <NavLink to="/app"  style={NavBarStyle}>App</NavLink>
                        </li>
                        <li className="nav-li">
                            <NavLink to="/others"  style={NavBarStyle}>Others</NavLink>
                        </li>
                    </ul>
                </div>
            </nav>
        </div>
    );
}

export default NavBar;
