import './NavBar.css'
import { Link, NavLink } from 'react-router-dom';
import { useEffect, useState } from 'react';



const NavBarStyle = ({isActive}) => {
    return{
        textDecoration: 'none',
        color: '#0078d7',
        fontSize: '20px',
        backgroundColor: isActive ? "#2da9ad": '',
        borderRadius: "12px",
        paddingTop: '10px,',
        paddingBottom: '10px',
        width: '100px',


    }
}

function NavBar() {
    const [location, setLocation] = useState("")

    useEffect(() => {
        console.log(window.location.pathname)
        setLocation(window.location.pathname)
    }, [location])

    return (
        <div id="nav-container">
            <nav id="navBar">
                <div className="ul-cont">
                            <NavLink to="/" style={NavBarStyle}>Home</NavLink>
                            <NavLink to="/projects"  style={NavBarStyle}>Projects</NavLink>
                            <NavLink to="/app"  style={NavBarStyle}>App</NavLink>
                            <NavLink to="/others"  style={NavBarStyle}>Others</NavLink>
                </div>
            </nav>
        </div>
    );
}

export default NavBar;
