import './NavBar.css'
import { NavLink } from 'react-router-dom';
import { useEffect, useState } from 'react';

const styles = {
    navStyle: {
        textDecoration: 'none',
        color: '#0078d7',
        fontSize: '20px',
        width: '100px',
        display: 'block',

    },
    currentPage: {
        backgroundColor: "#2da9ad",
        borderRadius: "12px",
        paddingTop: '10px,',
        paddingBottom: '10px',
        paddingRight: '10px',
        display: 'block',
    },
    linkContainer: {
        width: '100px',
    }
}

const NavBarStyle = ({isActive}) => {
    return{
        textDecoration: 'none',
        color: '#0078d7',
        fontSize: '20px',
        backgroundColor: isActive ? "#2da9ad": '',
        borderRadius: "12px",
        paddingTop: '10px,',
        paddingBottom: '10px',
    }
}

function NavBar() {
    const [location, setLocation] = useState("")

    useEffect(() => {
        console.log(window.location.pathname)
        setLocation(window.location.pathname)
    }, [location])

    return (
        <div id="nav-container">
            <nav id="navBar">
                <div className="ul-cont">
                            <NavLink to="/" style={styles.navStyle} activeStyle={styles.currentPage} exact >Home</NavLink>
                            <NavLink to="/projects" style={styles.navStyle} activeStyle={styles.currentPage}>Projects</NavLink>
                            <NavLink to="/app" >App</NavLink>
                            <NavLink to="/others" >Others</NavLink>
                </div>
            </nav>
        </div>
    );
}

export default NavBar;
