import React from 'react';
import textColor from "../../../styles/Color";

const styles = {
    container: {
        textAlign: 'left',
        marginBottom: '100px',
    },
    titaleText: {
        color: textColor.MainBlue,
        fontSize: '25px',
        fontWeight: 'bold',
    },
    lineBreak: {
        width: '200px',
        textAlign: 'left',
    },
    jobTitle: {
        color: 'gray',
        fontSize: '18px',
    },
    descriptionText: {
        color: 'white',
        marginTop: '10px',
        lineHeight: '30px',
    },
    toolsContainer: {
        display: 'flex',
        width: '100%',        
        flowDirection: 'column',
    },
    toolCont: {
        color: 'white',
        width: '80px',
        backgroundColor: 'green',
    }
}

function SmallDisplayBox(props) {
    return (
        <div style={styles.container}>
            <p style={styles.titaleText}> {props.title} </p>
            <div style={styles.lineBreak}>
                <hr></hr>
            </div>
            <p style={styles.jobTitle}>{props.jobTitle}</p>
            <div style={styles.descriptionText}>
                {makeDescriptionList(props.description)}
            </div>
            <div>
                {makeToolsList(props.tools)}
            </div>
        </div>
    )
}

function makeDescriptionList(array) {
    return (
        <div>
            <ul>
                {array.map((item, index) => (
                    <li key={index}>{item}</li>
                ))}
            </ul>
        </div>
    )
}

function makeToolsList(array) {
    return (
        <div styles={styles.toolsContainer}>
                {array.map((item, index) => (
                    <div style={styles.toolCont} key={index}>{item}</div>
                ))}
        </div>
    )
}


export default SmallDisplayBox;
