import './NavBar.css'
import { Link, NavLink } from 'react-router-dom';
import { useEffect, useState } from 'react';

const styles = {
    navStyle: {
        textDecoration: 'none',
        color: '#0078d7',
        fontSize: '20px',
    },
    currentPage: {
        backgroundColor: "#2da9ad",
        borderRadius: "12px",
        paddingTop: '10px,',
        paddingBottom: '10px',
    },
}

const NavBarStyle = ({isActive}) => {
    navStyle = {
        textDecoration: 'none',
        color: '#0078d7',
        fontSize: '20px',
        backgroundColor: isActive ? "#2da9ad": '',
        borderRadius: "12px",
        paddingTop: '10px,',
        paddingBottom: '10px',
    }
}

function NavBar() {
    const [location, setLocation] = useState("")

    useEffect(() => {
        console.log(window.location.pathname)
        setLocation(window.location.pathname)
    }, [location])

    return (
        <div id="nav-container">
            <nav id="navBar">
                <div className="ul-cont">
                    <ul id="navLi-container">
                        <li className="nav-li">
                            <NavLink to="/" style={NavBarStyle}>Home</NavLink>
                        </li>
                        <li className="nav-li">
                            <NavLink to="/projects" avtiveClassName="currentPage" className='link-style'>Projects</NavLink>
                        </li>
                        <li className="nav-li">
                            <Link to="/app" className='link-style'>App</Link>
                        </li>
                        <li className="nav-li">
                            <Link to="/others" className='link-style'>Others</Link>
                        </li>
                    </ul>
                </div>
            </nav>
        </div>
    );
}

export default NavBar;
